// App.js
import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes, Link, useNavigate } from 'react-router-dom'; // useNavigate 추가
import MainPage from './component/MainPage';
import DetailPage from './component/DetailPage';
import LoginPage from './component/LoginPage';
import SignupPage from './component/SignupPage';
import './App.css';

const App = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const navigate = useNavigate();

  const handleSearch = () => {
    // 검색 버튼 클릭 시 원하는 경로로 이동하도록 설정
    navigate(`/search/${searchTerm}`);
  };

  return (
    <Router>
      <div>
        <header>
          <h1>
            <Link to="/">TRY</Link>
          </h1>
          <div className="search-container">
            <input
              type="text"
              placeholder="Search..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
            />
            <button onClick={handleSearch}>Search</button>
          </div>
          <nav>
            <Link to="/login">Login</Link>
          </nav>
        </header>
        <nav>
          <ul>
            <li><Link to="/category/politics">Politics</Link></li>
            <li><Link to="/category/economy">Economy</Link></li>
            <li><Link to="/category/society">Society</Link></li>
            <li><Link to="/category/it">IT</Link></li>
            <li><Link to="/category/world">World</Link></li>
            <li><Link to="/category/culture">Culture</Link></li>
          </ul>
        </nav>
        <div className="container">
          <Routes>
            <Route path="/" element={<MainPage />} />
            <Route path="/detail" element={<DetailPage />} />
            <Route path="/login" element={<LoginPage />} />
            <Route path="/signup" element={<SignupPage />} />
          </Routes>
        </div>
        <div className="footer">
          © 2023 News App. All rights reserved.
        </div>
      </div>
    </Router>
  );
};

export default App;
