// MyPage.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Modal from 'react-modal';
import '../css/MyPage.css';

const MyPage = () => {
  const navigate = useNavigate();
  const [userData, setUserData] = useState({
    isLoggedIn: true,
    userInfo: {
      age: 25,
      gender: 'Male',
      interests: ['Programming', 'Technology', 'Sports', 'Music', 'Movies', 'Books', 'Travel', 'Food', 'Art', 'Science'],
      recentNews: [
        'News 1',
        'News 2',
        'News 3',
        'News 4',
        'News 5',
        'News 6',
        'News 7',
        'News 8',
        'News 9',
        'News 10',
      ],
    },
  });
  const [isWithdrawalModalOpen, setWithdrawalModalOpen] = useState(false);

  const handleWithdrawal = () => {
    setWithdrawalModalOpen(true);
  };

  const handleConfirmWithdrawal = () => {
    setUserData({
      isLoggedIn: false,
      userInfo: {},
    });
    setWithdrawalModalOpen(false);
  };

  const handleCloseModal = () => {
    setWithdrawalModalOpen(false);
  };

  if (!userData.isLoggedIn) {
    navigate('/login');
    return null;
  }

  const { age, gender, interests, recentNews } = userData.userInfo;

  return (
    <div className="my-page-container">
      <h2 className="my-page-header">My Page</h2>
      <div className="my-page-info">
        <p>Age: {age}</p>
        <p>Gender: {gender}</p>
        <p>
          Interests: {interests.slice(0, 10).map((interest, index) => (
            <span key={index}>{`#${interest} `}</span>
          ))}
        </p>
        <p>Recent News: {recentNews.slice(0, 10).join(', ')}</p>
      </div>
      <div className="my-page-buttons">
        <button onClick={handleWithdrawal}>회원 탈퇴</button>
      </div>

      {/* 회원탈퇴 확인 모달 */}
      <Modal
        isOpen={isWithdrawalModalOpen}
        onRequestClose={handleCloseModal}
        contentLabel="Withdrawal Confirmation"
        style={{ content: { width: '300px', height: '150px', margin: 'auto' } }}
        className="my-page-modal-content"
      >
        <h2>탈퇴하시겠습니까?</h2>
        <div className="my-page-modal-buttons">
          <button onClick={handleConfirmWithdrawal}>확인</button>
          <button onClick={handleCloseModal}>취소</button>
        </div>
      </Modal>
    </div>
  );
};

export default MyPage;
