// App.js
import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';
import MainPage from './component/MainPage';
import DetailPage from './component/DetailPage';
import LoginPage from './component/LoginPage';
import SignupPage from './component/SignupPage';
import MyPage from './component/MyPage';
import './App.css';

const App = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  const handleLogin = () => {
    // 로그인 로직 처리 후
    setIsLoggedIn(true);
  };

  const handleLogout = () => {
    // 로그아웃 로직 처리 후
    setIsLoggedIn(false);
  };

  return (
    <Router>
      <div>
        <header>
          <div className="center">
            <h1><Link to="/">TRY</Link></h1>
            <div className="search-box">
              <input type="text" placeholder="Search" />
              <button type="button">검색</button>
            </div>
            <nav>
              <Link to="/">홈</Link>
              <Link to="/category/politics">정치</Link>
              <Link to="/category/economy">경제</Link>
              <Link to="/category/society">사회</Link>
              <Link to="/category/it">IT</Link>
              <Link to="/category/world">세계</Link>
              <Link to="/category/culture">문화</Link>
              {isLoggedIn ? (
                <>
                  <Link to="/mypage">마이페이지</Link>
                  <button onClick={handleLogout}>로그아웃</button>
                </>
              ) : (
                <>
                  <Link to="/login">로그인</Link>
                  <Link to="/signup">회원가입</Link>
                </>
              )}
            </nav>
          </div>
        </header>
        <div className="container">
          <Routes>
            <Route path="/" element={<MainPage />} />
            <Route path="/detail/:id" element={<DetailPage />} />
            <Route path="/login" element={<LoginPage />} />
            <Route path="/signup" element={<SignupPage />} />
            <Route path="/mypage" element={<MyPage />} />
          </Routes>
        </div>
        <div className="footer">
          © 2023 News App. All rights reserved.
        </div>
      </div>
    </Router>
  );
};

export default App;
