import React, { useState } from 'react';
import Modal from 'react-modal';

const MyPage = () => {
  // 모의 데이터
  const mockUserData = {
    isLoggedIn: true,
    userInfo: {
      age: 25,
      gender: 'Male',
      interests: ['Programming', 'Technology', 'Sports', 'Music', 'Movies', 'Books', 'Travel', 'Food', 'Art', 'Science'],
      recentNews: [
        'News 1',
        'News 2',
        'News 3',
        'News 4',
        'News 5',
        'News 6',
        'News 7',
        'News 8',
        'News 9',
        'News 10',
      ],
    },
  };

  // 상태
  const [userData, setUserData] = useState(mockUserData);
  const [isWithdrawalModalOpen, setWithdrawalModalOpen] = useState(false);
  const [withdrawalCompleted, setWithdrawalCompleted] = useState(false);

  // 회원탈퇴 함수
  const handleWithdrawal = () => {
    // '탈퇴하시겠습니까?' 모달 열기
    setWithdrawalModalOpen(true);
  };

  // 실제 회원탈퇴 처리 함수
  const handleConfirmWithdrawal = () => {
    // 실제로는 서버와 통신하여 회원탈퇴 처리를 해야합니다.
    // 여기서는 간단히 상태를 초기화하고 모달을 닫습니다.
    setUserData({
      isLoggedIn: false,
      userInfo: {},
    });
    setWithdrawalCompleted(true);
    setWithdrawalModalOpen(false);
  };

  // 모달 닫기 함수
  const handleCloseModal = () => {
    setWithdrawalModalOpen(false);
    setWithdrawalCompleted(false);
  };

  // 로그인 상태 체크
  if (!userData.isLoggedIn) {
    // 로그인되지 않은 상태
    return (
      <div>
        <h2>Login Page</h2>
        {/* 로그인 페이지 UI 및 기능 추가 */}
      </div>
    );
  }

  // 로그인된 상태
  const { age, gender, interests, recentNews } = userData.userInfo;

  // 모달 스타일
  const modalStyle = {
    content: {
      width: '300px', // 원하는 크기로 조절
      margin: 'auto', // 가로 중앙 정렬
      top: '50%', // 세로 중앙 정렬을 위해 50% 위치로 이동
      left: '50%', // 가로 중앙 정렬을 위해 50% 위치로 이동
      transform: 'translate(-50%, -50%)', // 정확한 중앙 정렬을 위한 transform
    },
  };

  return (
    <div>
      <h2>My Page</h2>
      <div>
        <p>Age: {age}</p>
        <p>Gender: {gender}</p>
        <p>
          Interests: {interests.slice(0, 10).map((interest, index) => (
            <span key={index}>{`#${interest} `}</span>
          ))}
        </p>
        <p>Recent News: {recentNews.slice(0, 10).join(', ')}</p>
      </div>
      <button onClick={handleWithdrawal}>회원 탈퇴</button>

      {/* 회원탈퇴 확인 모달 */}
      <Modal
        isOpen={isWithdrawalModalOpen}
        onRequestClose={handleCloseModal}
        contentLabel="Withdrawal Confirmation"
        style={modalStyle}
      >
        {withdrawalCompleted ? (
          <>
            <h2>회원탈퇴 되었습니다.</h2>
            <button onClick={handleCloseModal}>확인</button>
          </>
        ) : (
          <>
            <h2>탈퇴하시겠습니까?</h2>
            <button onClick={handleConfirmWithdrawal}>확인</button>
            <button onClick={handleCloseModal}>취소</button>
          </>
        )}
      </Modal>
    </div>
  );
};

export default MyPage;
