import React, { useState } from 'react';

const MyPage = () => {
  // 모의 데이터
  const mockUserData = {
    isLoggedIn: true,
    userInfo: {
      age: 25,
      gender: 'Male',
      interests: ['Programming', 'Technology', 'Sports', 'Music', 'Movies', 'Books', 'Travel', 'Food', 'Art', 'Science'],
      recentNews: [
        'News 1',
        'News 2',
        'News 3',
        'News 4',
        'News 5',
        'News 6',
        'News 7',
        'News 8',
        'News 9',
        'News 10',
      ],
    },
  };

  // 상태
  const [userData, setUserData] = useState(mockUserData);

  // 회원탈퇴 함수
  const handleWithdrawal = () => {
    // 실제로는 서버와 통신하여 회원탈퇴 처리를 해야합니다.
    // 여기서는 간단히 상태를 초기화합니다.
    setUserData({
      isLoggedIn: false,
      userInfo: {},
    });
  };

  // 로그인 상태 체크
  if (!userData.isLoggedIn) {
    // 로그인되지 않은 상태
    return (
      <div>
        <h2>Login Page</h2>
        {/* 로그인 페이지 UI 및 기능 추가 */}
      </div>
    );
  }

  // 로그인된 상태
  const { age, gender, interests, recentNews } = userData.userInfo;

  return (
    <div>
      <h2>My Page</h2>
      <div>
        <p>Age: {age}</p>
        <p>Gender: {gender}</p>
        <p>
          Interests: {interests.slice(0, 10).map((interest, index) => (
            <span key={index}>{`#${ interest}`}</span>
          ))}
        </p>
        <p>Recent News: {recentNews.slice(0, 10).join(', ')}</p>
      </div>
      <button onClick={handleWithdrawal}>Withdrawal</button>
    </div>
  );
};

export default MyPage;
